mapper.update:
update tb_newbee_mall_goods_info
set goods_sell_status=#{sellStatus},update_time=now() where goods_id in

    #{id}

mapper.select:
select

from tb_newbee_mall_goods_info


        and goods_name like CONCAT('%',#{goodsName},'%')


        and goods_sell_status = #{goodsSellStatus}


        and create_time > #{startTime}


        and create_time < #{endTime}


order by goods_id desc

    limit #{start},#{limit}

select

from tb_newbee_mall_goods_info


        and (goods_name like CONCAT('%',#{keyword},'%') or goods_intro like CONCAT('%',#{keyword},'%'))


        and goods_category_id = #{goodsCategoryId}


        and goods_sell_status = #{goodsSellStatus}






            order by goods_id desc



            order by selling_price asc



            order by stock_num desc




    limit #{start},#{limit}

select ca.category_id, go.goods_id, go.goods_name, go.goods_intro, go.goods_category_id, go.goods_cover_img, go.goods_carousel, go.original_price, go.selling_price, go.stock_num, go.tag, go.goods_sell_status, go.create_user, go.create_time, go.update_user, go.update_time from tb_newbee_mall_goods_category as ca join tb_newbee_mall_goods_info as go on ca.category_id = go.goods_category_id
and ca.parent_id = #{goodsCategoryId}
order by ca.category_id

select count(*) from tb_newbee_mall_goods_info


        and (goods_name like CONCAT('%',#{keyword},'%') or goods_intro like CONCAT('%',#{keyword},'%'))


        and goods_category_id = #{goodsCategoryId}


        and goods_sell_status = #{goodsSellStatus}

select

from tb_newbee_mall_goods_info
where goods_id in

    #{id}

order by field(goods_id,

    #{id}

);

select count(*) from tb_newbee_mall_goods_info


        and goods_name like CONCAT('%','#{goodsName}','%')


        and goods_sell_status = #{goodsSellStatus}


        and create_time > #{startTime}


        and create_time < #{endTime}

 SELECT id,path
FROM goods_image
	where id = #{goodsId,jdbcType=BIGINT}

	 SELECT id,star,customer_id,comment_date,goods_id,title,content,picture
 		FROM review where goods_id = #{goodsId,jdbcType=BIGINT}
 		order by star DESC
